import React, { useState } from 'react';

function MicrobiologyForm() {
  const [formData, setFormData] = useState({
    micro_organism_1: '',
    micro_organism_2: '',
    isolate_1_sensitive: [],
    isolate_1_resistant: [],
    isolate_1_intermediate: [],
    isolate_2_sensitive: [],
    isolate_2_resistant: [],
    isolate_2_intermediate: [],
    secondary_bsi: ''  // New field for the BSI question
  });

  const antibiotics = ['Cefuroxime', 'Ciprofloxacin', 'Amoxicillin', 'Penicillin'];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSelectChange = (e) => {
    const { name, options } = e.target;
    const selectedValues = Array.from(options).filter(option => option.selected).map(option => option.value);
    setFormData({
      ...formData,
      [name]: selectedValues
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Submitted:', formData);
  };

  return (
    <div style={styles.container}>
      <h2>Microbiology Form</h2>
      <form onSubmit={handleSubmit}>
        <div style={styles.formGroup}>
          <label htmlFor="micro_organism_1" style={styles.label}>Micro-organism implicated for SSI event 1:</label>
          <input
            type="text"
            id="micro_organism_1"
            name="micro_organism_1"
            value={formData.micro_organism_1}
            onChange={handleInputChange}
            style={styles.input}
          />
        </div>

        <div style={styles.formGroup}>
          <label htmlFor="micro_organism_2" style={styles.label}>Micro-organism implicated for SSI event 2:</label>
          <input
            type="text"
            id="micro_organism_2"
            name="micro_organism_2"
            value={formData.micro_organism_2}
            onChange={handleInputChange}
            style={styles.input}
          />
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label}>Whether Secondary BSI contributed to the death of the patient:</label>
          <label>
            <input
              type="radio"
              name="secondary_bsi"
              value="Yes"
              checked={formData.secondary_bsi === 'Yes'}
              onChange={handleInputChange}
            /> Yes
          </label>
          <label style={{ marginLeft: '20px' }}>
            <input
              type="radio"
              name="secondary_bsi"
              value="No"
              checked={formData.secondary_bsi === 'No'}
              onChange={handleInputChange}
            /> No
          </label>
        </div>

        <table style={styles.table}>
          <thead>
            <tr>
              <th colSpan="3">Isolate 1 (Antibiotic Susceptibility Pattern)</th>
              <th colSpan="3">Isolate 2 (Antibiotic Susceptibility Pattern)</th>
            </tr>
            <tr>
              <th>Sensitive</th>
              <th>Resistant</th>
              <th>Intermediate</th>
              <th>Sensitive</th>
              <th>Resistant</th>
              <th>Intermediate</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              {/* Isolate 1 */}
              <td>
                <select
                  name="isolate_1_sensitive"
                  multiple
                  value={formData.isolate_1_sensitive}
                  onChange={handleSelectChange}
                  style={styles.select}
                >
                  {antibiotics.map((antibiotic) => (
                    <option key={antibiotic} value={antibiotic}>
                      {antibiotic}
                    </option>
                  ))}
                </select>
              </td>
              <td>
                <select
                  name="isolate_1_resistant"
                  multiple
                  value={formData.isolate_1_resistant}
                  onChange={handleSelectChange}
                  style={styles.select}
                >
                  {antibiotics.map((antibiotic) => (
                    <option key={antibiotic} value={antibiotic}>
                      {antibiotic}
                    </option>
                  ))}
                </select>
              </td>
              <td>
                <select
                  name="isolate_1_intermediate"
                  multiple
                  value={formData.isolate_1_intermediate}
                  onChange={handleSelectChange}
                  style={styles.select}
                >
                  {antibiotics.map((antibiotic) => (
                    <option key={antibiotic} value={antibiotic}>
                      {antibiotic}
                    </option>
                  ))}
                </select>
              </td>

              {/* Isolate 2 */}
              <td>
                <select
                  name="isolate_2_sensitive"
                  multiple
                  value={formData.isolate_2_sensitive}
                  onChange={handleSelectChange}
                  style={styles.select}
                >
                  {antibiotics.map((antibiotic) => (
                    <option key={antibiotic} value={antibiotic}>
                      {antibiotic}
                    </option>
                  ))}
                </select>
              </td>
              <td>
                <select
                  name="isolate_2_resistant"
                  multiple
                  value={formData.isolate_2_resistant}
                  onChange={handleSelectChange}
                  style={styles.select}
                >
                  {antibiotics.map((antibiotic) => (
                    <option key={antibiotic} value={antibiotic}>
                      {antibiotic}
                    </option>
                  ))}
                </select>
              </td>
              <td>
                <select
                  name="isolate_2_intermediate"
                  multiple
                  value={formData.isolate_2_intermediate}
                  onChange={handleSelectChange}
                  style={styles.select}
                >
                  {antibiotics.map((antibiotic) => (
                    <option key={antibiotic} value={antibiotic}>
                      {antibiotic}
                    </option>
                  ))}
                </select>
              </td>
            </tr>
          </tbody>
        </table>

        <button type="submit" style={styles.button}>Submit</button>
      </form>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '1000px',
    margin: 'auto',
    padding: '20px',
    border: '1px solid #000',
    borderRadius: '10px',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
    fontFamily: 'Arial, sans-serif'
  },
  formGroup: {
    marginBottom: '20px'
  },
  label: {
    fontWeight: 'bold',
    display: 'inline-block',
    marginRight: '10px',
    width: '250px'
  },
  input: {
    width: '300px',
    padding: '5px',
    marginRight: '10px'
  },
  table: {
    borderCollapse: 'collapse',
    width: '100%',
    marginTop: '20px'
  },
  select: {
    width: '100%',
    padding: '5px'
  },
  button: {
    marginTop: '20px',
    padding: '10px 20px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    cursor: 'pointer'
  }
};

export default MicrobiologyForm;
