import React from 'react';
import { useNavigate } from 'react-router-dom';
import './MainPage.css'; // Import CSS file for styling

const MainPage = () => {
  const navigate = useNavigate();

  const handleClick = (role) => {
    navigate(`/${role}`);
  };

  return (
    <div className="main-page">
      <h1 className="welcome-text">Welcome to the Hospital Portal</h1>
      <div className="options">
        <button className="role-button" onClick={() => handleClick('nurse')}>Nurse</button>
        <button className="role-button" onClick={() => handleClick('junior-doctor')}>Junior Doctor</button>
        <button className="role-button" onClick={() => handleClick('senior-doctor')}>Senior Doctor</button>
      </div>

      {/* Dashboard Section */}
      <div className="dashboard">
        <h2>About Our Hospital</h2>
        <p>
          Welcome to our state-of-the-art hospital, dedicated to providing top-quality care and services.
          Our team of dedicated professionals work around the clock to ensure the well-being of our patients.
          We offer a range of services, from emergency care to specialized treatments, ensuring that every patient's needs are met.
        </p>
        <h3>Explore More</h3>
        <ul>
          <li>Department Directory</li>
          <li>Emergency Services</li>
          <li>Contact Us</li>
          <li>Patient Resources</li>
        </ul>
      </div>
    </div>
  );
};

export default MainPage;
