import React, { useState } from 'react';

function MicrobiologyForm() {
  const [formData, setFormData] = useState({
    micro_organism: '',
    antibioticData: []
  });

  // List of microorganisms
  const microorganisms = [
    "E.COLI",
    "KLEBSIELLA PNEUMONIAE",
    "ENTEROCOCCUS FAECIUM",
    "ENTEROCOCCUS FAECALIS",
    "STAPHYLOCOCCUS HAEMOLYTICUS",
    "SKIN COMMENSAL FLORA",
    "PSEUDOMONAS AERUGINOSA",
    "STAPHYLOCOCCUS AUREUS (MRSA)",
    "STAPHYLOCOCCUS AUREUS (MSSA)",
    "CONS",
    "ACINETOBACTER BAUMANII",
    "CITROBACTER KOSERI",
    "CITROBACTER FREUNDII",
    "ENTEROBACTER CLOACAE",
    "ENTEROBACTER AEROGENES",
    "PROTEUS MIRABILIS",
    "MORGANELLA MORGANII",
    "Others"
  ];

  // List of antibiotics
  const antibiotics = [
    "Amoxicillin-clavulanic acid", "Amikacin", "Aztreonam", "Cefepime", 
    "Ceftazidime", "Ceftriaxone", "Netilmicin", "Meropenem", "Imipenem", 
    "Norfloxacin", "Ciprofloxacin", "Levofloxacin", "Penicillin", "Oxacillin",
    "Gentamicin", "Tetracycline", "Clindamycin", "Linezolid", "Nitrofurantoin",
    "Erythromycin", "Cefoxitin", "Co-trimoxazole", "Fosfomycin", "Colistin"
  ];

  // Handle change in microorganism dropdown
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Handle antibiotic data change
  const handleAntibioticDataChange = (index, field, value) => {
    const updatedAntibioticData = [...formData.antibioticData];
    updatedAntibioticData[index] = {
      ...updatedAntibioticData[index],
      [field]: value
    };
    setFormData({
      ...formData,
      antibioticData: updatedAntibioticData
    });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Submitted:', formData);
  };

  return (
    <div style={styles.container}>
      <h2>Microbiology Form</h2>
      <form onSubmit={handleSubmit}>
        {/* Micro-organism dropdown */}
        <div style={styles.formGroup}>
          <label htmlFor="micro_organism" style={styles.label}>Micro-organism implicated for SSI event:</label>
          <select
            id="micro_organism"
            name="micro_organism"
            value={formData.micro_organism}
            onChange={handleInputChange}
            style={styles.select}
          >
            <option value="">Select a Micro-organism</option>
            {microorganisms.map((organism, index) => (
              <option key={index} value={organism}>
                {organism}
              </option>
            ))}
          </select>
        </div>

        {/* Antibiotic Susceptibility Table */}
        <table style={styles.table}>
          <thead>
            <tr>
              <th>Antibiotic</th>
              <th>MIC</th>
              <th>Interpretation</th>
            </tr>
          </thead>
          <tbody>
            {antibiotics.map((antibiotic, index) => (
              <tr key={index}>
                <td>{antibiotic}</td>
                <td>
                  <input
                    type="text"
                    name={`mic_${index}`}
                    value={formData.antibioticData[index]?.mic || ''}
                    onChange={(e) => handleAntibioticDataChange(index, 'mic', e.target.value)}
                    style={styles.input}
                  />
                </td>
                <td>
                  <select
                    name={`interpretation_${index}`}
                    value={formData.antibioticData[index]?.interpretation || ''}
                    onChange={(e) => handleAntibioticDataChange(index, 'interpretation', e.target.value)}
                    style={styles.select}
                  >
                    <option value="">Select Interpretation</option>
                    <option value="Resistant">Resistant</option>
                    <option value="Sensitive">Sensitive</option>
                    <option value="Intermediate">Intermediate</option>
                  </select>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <button type="submit" style={styles.button}>Submit</button>
      </form>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '1000px',
    margin: 'auto',
    padding: '20px',
    border: '1px solid #000',
    borderRadius: '10px',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
    fontFamily: 'Arial, sans-serif'
  },
  formGroup: {
    marginBottom: '20px'
  },
  label: {
    fontWeight: 'bold',
    display: 'inline-block',
    marginRight: '10px',
    width: '250px'
  },
  select: {
    width: '100%',
    padding: '5px'
  },
  input: {
    width: '100%',
    padding: '5px'
  },
  button: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '15px 30px',
    border: 'none',
    cursor: 'pointer',
    fontSize: '16px',
    borderRadius: '10px'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    marginBottom: '20px'
  }
};

export default MicrobiologyForm;
