import React, { useState } from 'react';

const EventDetailsForm = () => {
  const [event_details_form, set_event_details_form] = useState({
    specific_event: {
      sip: false,
      sis: false,
      dip: false,
      dis: false,
      organ_space: {
        intra_abdominal: false,
        intravascular: false,
        central_nervous_system: false,
        respiratory: false,
        other: false,
      },
    },
    detected: {
      during_admission: false,
      post_discharge_surveillance: false,
      readmission_facility: false,
    },
    sample_type: '',
    secondary_bsi_contribution: '', // Field for Secondary BSI contribution
  });

  const handle_event_change = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === 'checkbox') {
      set_event_details_form((prev) => ({
        ...prev,
        specific_event: { ...prev.specific_event, [name]: checked },
      }));
    } else {
      set_event_details_form((prev) => ({
        ...prev,
        specific_event: { ...prev.specific_event, [name]: value },
      }));
    }
  };

  const handle_organ_space_change = (e) => {
    const { name, checked } = e.target;
    set_event_details_form((prev) => ({
      ...prev,
      specific_event: {
        ...prev.specific_event,
        organ_space: { ...prev.specific_event.organ_space, [name]: checked },
      },
    }));
  };

  const handle_detected_change = (e) => {
    const { name, checked } = e.target;
    set_event_details_form((prev) => ({
      ...prev,
      detected: { ...prev.detected, [name]: checked },
    }));
  };

  const handle_sample_type_change = (e) => {
    set_event_details_form((prev) => ({
      ...prev,
      sample_type: e.target.value, 
    }));
  };

  const handle_secondary_bsi_change = (e) => {
    set_event_details_form((prev) => ({
      ...prev,
      secondary_bsi_contribution: e.target.value,
    }));
  };

  return (
    <form style={styles.eventDetailsForm}>
      <h2 style={styles.heading}>Event Details Form</h2>

      <h3 style={styles.subheading}>Specific Event:</h3>
      <div style={styles.formRow}>
        <label>
          <input
            type="checkbox"
            name="sip"
            checked={event_details_form.specific_event.sip}
            onChange={handle_event_change}
          />
          Superficial Incisional Primary (SIP)
        </label>
        <label>
          <input
            type="checkbox"
            name="sis"
            checked={event_details_form.specific_event.sis}
            onChange={handle_event_change}
          />
          Superficial Incisional Secondary (SIS)
        </label>
        <label>
          <input
            type="checkbox"
            name="dip"
            checked={event_details_form.specific_event.dip}
            onChange={handle_event_change}
          />
          Deep Incisional Primary (DIP)
        </label>
        <label>
          <input
            type="checkbox"
            name="dis"
            checked={event_details_form.specific_event.dis}
            onChange={handle_event_change}
          />
          Deep Incisional Secondary (DIS)
        </label>
      </div>

      <h4 style={styles.subheading}>Organ/Space:</h4>
      <div style={styles.formRow}>
        <label>
          <input
            type="checkbox"
            name="intra_abdominal"
            checked={event_details_form.specific_event.organ_space.intra_abdominal}
            onChange={handle_organ_space_change}
          />
          Intra-abdominal
        </label>
        <label>
          <input
            type="checkbox"
            name="intravascular"
            checked={event_details_form.specific_event.organ_space.intravascular}
            onChange={handle_organ_space_change}
          />
          Intravascular
        </label>
        <label>
          <input
            type="checkbox"
            name="central_nervous_system"
            checked={event_details_form.specific_event.organ_space.central_nervous_system}
            onChange={handle_organ_space_change}
          />
          Central Nervous System
        </label>
        <label>
          <input
            type="checkbox"
            name="respiratory"
            checked={event_details_form.specific_event.organ_space.respiratory}
            onChange={handle_organ_space_change}
          />
          Respiratory
        </label>
        <label>
          <input
            type="checkbox"
            name="other"
            checked={event_details_form.specific_event.organ_space.other}
            onChange={handle_organ_space_change}
          />
          Other
        </label>
      </div>

      <h3 style={styles.subheading}>Detected:</h3>
      <div style={styles.formRow}>
        <label>
          <input
            type="checkbox"
            name="during_admission"
            checked={event_details_form.detected.during_admission}
            onChange={handle_detected_change}
          />
          A (During admission)
        </label>
        <label>
          <input
            type="checkbox"
            name="post_discharge_surveillance"
            checked={event_details_form.detected.post_discharge_surveillance}
            onChange={handle_detected_change}
          />
          P (Post-discharge surveillance)
        </label>
        <label>
          <input
            type="checkbox"
            name="readmission_facility"
            checked={event_details_form.detected.readmission_facility}
            onChange={handle_detected_change}
          />
          RF (Readmission to facility where procedure performed)
        </label>
      </div>

      {/* Dropdown for Sample Types */}
      <div style={styles.formRow}>
        <label style={styles.label}>Sample Type:</label>
        <select
          name="sample_type"
          value={event_details_form.sample_type}
          onChange={handle_sample_type_change}
          style={styles.formInput}
        >
          <option value="">Select Sample Type</option>
          <option value="PUS SWAB">PUS SWAB</option>
          <option value="PUS">PUS</option>
          <option value="TISSUE">TISSUE</option>
          <option value="DRAIN FLUID">DRAIN FLUID</option>
          <option value="ASCITIC FLUID">ASCITIC FLUID</option>
          <option value="BILE FLUID">BILE FLUID</option>
          <option value="OTHERS">OTHERS</option>
        </select>
      </div>

      {/* New field for Secondary BSI Contribution */}
      <div style={styles.formRow}>
        <label style={styles.label}>Whether Secondary BSI contributed to the death of the patient:</label>
        <label>
          <input
            type="radio"
            name="secondary_bsi_contribution"
            value="Yes"
            checked={event_details_form.secondary_bsi_contribution === 'Yes'}
            onChange={handle_secondary_bsi_change}
          />
          Yes
        </label>
        <label>
          <input
            type="radio"
            name="secondary_bsi_contribution"
            value="No"
            checked={event_details_form.secondary_bsi_contribution === 'No'}
            onChange={handle_secondary_bsi_change}
          />
          No
        </label>
      </div>

      <button type="submit" style={styles.submitButton}>Submit</button>
    </form>
  );
};

const styles = {
  eventDetailsForm: {
    maxWidth: '600px',
    margin: '0 auto',
    padding: '20px',
    backgroundColor: '#f4f4f4',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
  heading: {
    color: '#333',
    marginBottom: '20px',
  },
  subheading: {
    color: '#444',
    marginBottom: '15px',
  },
  formRow: {
    display: 'flex',
    flexDirection: 'column',
    marginBottom: '20px',
  },
  label: {
    fontWeight: 'bold',
    marginBottom: '8px',
  },
  formInput: {
    padding: '8px',
    border: '1px solid #ccc',
    borderRadius: '4px',
    boxSizing: 'border-box',
  },
  submitButton: {
    padding: '10px 20px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    marginTop: '20px',
  },
};

export default EventDetailsForm;
