import React from 'react';
import './MainPage.css';

const images = [
  require('./image1.png'),
  require('./image2.png'),
  require('./image3.png')
];

const MainPage = () => {
  const [currentIndex, setCurrentIndex] = React.useState(0);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  return (
    <div className="main-page">
      <div className="slideshow-container">
        <img src={images[currentIndex]} alt="Slideshow" className="slideshow-image" />
        <button className="prev" onClick={prevSlide}>❮</button>
        <button className="next" onClick={nextSlide}>❯</button>
      </div>
      <div className="main-buttons">
        <button>Nurse</button>
        <button>Junior Doctor</button>
        <button>Senior Doctor</button>
      </div>
      <div className="options">
        <button>Surveillance Form</button>
        <button>Surgery Form</button>
        <button>Antibiotic Form</button>
        <button>Microbiology Form</button>
        <button>Event Details Form</button>
        <button>SSI Evaluation Form</button>
      </div>
    </div>
  );
};

export default MainPage;
