import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './MainPage.css'; // Ensure the CSS file is updated
import logo from './logo.png'; // Ensure the path to your logo is correct

const MainPage = () => {
  const [activeSection, setActiveSection] = useState(null);
  const navigate = useNavigate();

  const handleSectionClick = (section) => {
    setActiveSection(activeSection === section ? null : section);
  };

  const handleRoleClick = (role) => {
    navigate(`/${role}`);
  };

  return (
    <div className="dashboard-container">
      {/* Header Section */}
      <header className="header">
        <div className="header-content">
          <img src={logo} alt="Hospital Logo" className="logo" />
          <nav className="nav-bar">
            <ul className="nav-list">
              <li onClick={() => handleSectionClick('about')}>About Us</li>
              <li onClick={() => handleSectionClick('contact')}>Contact Us</li>
              <li onClick={() => handleSectionClick('locations')}>Locations</li>
            </ul>
          </nav>
        </div>
      </header>

      {/* Dynamic Content */}
      <div className="dynamic-content">
        {activeSection === 'about' && (
          <div className="section-content">
            <h2>About Us</h2>
            <p>
              Welcome to our hospital! We provide top-quality care to all our patients using the latest
              medical technology and a compassionate approach.
            </p>
          </div>
        )}
        {activeSection === 'contact' && (
          <div className="section-content">
            <h2>Contact Us</h2>
            <p>Phone: 1800 102 9103<br />Email: contact@hospital.com</p>
          </div>
        )}
        {activeSection === 'locations' && (
          <div className="section-content">
            <h2>Locations</h2>
            <p>Find our hospital branches in New Delhi, Mumbai, and Bengaluru.</p>
          </div>
        )}
      </div>

      {/* Main Content */}
      <div className="main-content">
        <h1 className="welcome-text">Welcome to the Hospital Portal</h1>
        <div className="options">
          <button className="role-button" onClick={() => handleRoleClick('nurse')}>Nurse</button>
          <button className="role-button" onClick={() => handleRoleClick('junior-doctor')}>Junior Doctor</button>
          <button className="role-button" onClick={() => handleRoleClick('senior-doctor')}>Senior Doctor</button>
        </div>
      </div>
    </div>
  );
};

export default MainPage;
