import React, { useState } from 'react';

const AntibioticForm = () => {
  const [antibiotic_form, set_antibiotic_form] = useState({
    prior_antibiotics: Array(3).fill({ name: '', route: '', duration: '', doses: '' }),
    pre_peri_antibiotics: Array(3).fill({ name: '', route: '', duration: '', doses: '' }),
    post_antibiotics: Array(6).fill({ name: '', route: '', duration: '', doses: '' }),
    time_induction: '',
    time_incision: '',
    end_time_surgery: '',
  });

  const handle_change = (e, section, index) => {
    const { name, value } = e.target;
    const updated_section = [...antibiotic_form[section]];
    updated_section[index][name] = value;
    set_antibiotic_form({ ...antibiotic_form, [section]: updated_section });
  };

  const handle_time_change = (e) => {
    const { name, value } = e.target;
    set_antibiotic_form({ ...antibiotic_form, [name]: value });
  };

  const antibiotics_list = [
    'Amoxicillin-clavulanic acid', 'Amikacin', 'Aztreonam', 'Cefepime', 'Ceftazidime',
    'Ceftriaxone', 'Netilmicin', 'Meropenem', 'Imipenem', 'Levofloxacin', 'Norfloxacin',
    'Ciprofloxacin', 'Cefoperazone/Sulbactum', 'Ticarcillin/Clavulanic acid', 
    'Piperacillin-tazobactum', 'Ceftazidime/Avibactam', 'Penicillin', 'Oxacillin', 
    'Gentamicin', 'Tetracycline', 'Clindamycin', 'Vancomycin E STRIP', 'Linezolid', 
    'Teicoplanin', 'Nitrofurantoin', 'Erythromycin', 'Cefoxitin', 'Co-trimoxazole', 
    'Netilmicin', 'Ertapenem', 'Chloramphenicol', 'Fosfomycin', 'Colistin E STRIP'
  ];

  const routes_of_administration = [
    'I/V', 'I/M', 'S/C', 'I/D', 'P/O', 'Local Application', 'Sub Lingual',
  ];

  const doses_options = ['01', '02', '03', '04', '05'];

  const render_antibiotic_fields = (section, count) => (
    Array.from({ length: count }).map((_, index) => (
      <div key={index} style={styles.formRow}>
        <select
          name="name"
          value={antibiotic_form[section][index].name}
          onChange={(e) => handle_change(e, section, index)}
          style={styles.formSelect}
        >
          <option value="">Select Antibiotic</option>
          {antibiotics_list.map((antibiotic, idx) => (
            <option key={idx} value={antibiotic}>{antibiotic}</option>
          ))}
        </select>
        <select
          name="route"
          value={antibiotic_form[section][index].route}
          onChange={(e) => handle_change(e, section, index)}
          style={styles.formSelect}
        >
          <option value="">Select Route</option>
          {routes_of_administration.map((route, idx) => (
            <option key={idx} value={route}>{route}</option>
          ))}
        </select>
        <input
          type="text"
          name="duration"
          placeholder="Duration"
          value={antibiotic_form[section][index].duration}
          onChange={(e) => handle_change(e, section, index)}
          style={styles.formInput}
        />
        <select
          name="doses"
          value={antibiotic_form[section][index].doses}
          onChange={(e) => handle_change(e, section, index)}
          style={styles.formSelect}
        >
          <option value="">Select Doses</option>
          {doses_options.map((dose, idx) => (
            <option key={idx} value={dose}>{dose}</option>
          ))}
        </select>
      </div>
    ))
  );

  return (
    <form style={styles.antibioticForm}>
      <h2 style={styles.heading}>Antibiotic Prescription Surveillance Form</h2>

      <h3 style={styles.subheading}>Antibiotics given prior to operation</h3>
      {render_antibiotic_fields('prior_antibiotics', 3)}

      <h3 style={styles.subheading}>Antibiotics given Pre/Perioperatively</h3>
      {render_antibiotic_fields('pre_peri_antibiotics', 3)}

      <h3 style={styles.subheading}>Antibiotics given after peri-operative</h3>
      {render_antibiotic_fields('post_antibiotics', 6)}

      <div style={styles.formRow}>
        <label style={styles.label}>Time of Induction:</label>
        <input
                   type="time"
                   name="time_induction"
                   value={antibiotic_form.time_induction}
                   onChange={handle_time_change}
                   style={styles.formInput}
                 />
               </div>
         
               <div style={styles.formRow}>
                 <label style={styles.label}>Time of Incision:</label>
                 <input
                   type="time"
                   name="time_incision"
                   value={antibiotic_form.time_incision}
                   onChange={handle_time_change}
                   style={styles.formInput}
                 />
               </div>
         
               <div style={styles.formRow}>
                 <label style={styles.label}>End Time of Surgery:</label>
                 <input
                   type="time"
                   name="end_time_surgery"
                   value={antibiotic_form.end_time_surgery}
                   onChange={handle_time_change}
                   style={styles.formInput}
                 />
               </div>
         
               <button type="submit" style={styles.submitButton}>Submit</button>
             </form>
           );
         };
         
         const styles = {
           antibioticForm: {
             maxWidth: '800px',
             margin: '0 auto',
             padding: '20px',
             backgroundColor: '#f9f9f9',
             borderRadius: '8px',
             boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
           },
           heading: {
             color: '#333',
             marginBottom: '20px',
           },
           subheading: {
             color: '#444',
             marginBottom: '15px',
           },
           formRow: {
             display: 'flex',
             alignItems: 'center',
             justifyContent: 'space-between',
             marginBottom: '20px',
           },
           formInput: {
             width: '23%',
             padding: '8px',
             border: '1px solid #ccc',
             borderRadius: '4px',
             boxSizing: 'border-box',
           },
           formSelect: {
             width: '23%',
             padding: '8px',
             border: '1px solid #ccc',
             borderRadius: '4px',
           },
           label: {
             fontWeight: 'bold',
             marginRight: '10px',
           },
           submitButton: {
             padding: '10px 20px',
             backgroundColor: '#007bff',
             color: 'white',
             border: 'none',
             borderRadius: '4px',
             cursor: 'pointer',
             marginTop: '20px',
           },
         };
         
         export default AntibioticForm;
