import React, { useState } from 'react';

const SSIEvaluationForm = () => {
  const [formData, setFormData] = useState({
    patient_name: '',
    patient_id: '',
    age: '',
    gender: '',
    procedure_name: '',
    procedure_date: '',
    operation_theatre: '',
    antibioticsGiven: '',
    evaluations: Array(16).fill({ yes: false, no: false, remarks: '' }), // Adjust array length as needed
    showAntibioticsField: false,
  });

  const procedureOptions = [
    'Abdominal aortic aneurysm repair', 'Limb amputation', 'Appendix surgery', 'Shunt for dialysis',
    'Bile duct, liver, or pancreas surgery', 'Carotid endarterectomy', 'Gallbladder surgery', 'Colon surgery',
    'Cesarean section', 'Gastric surgery', 'Heart transplant', 'Abdominal hysterectomy', 'Kidney transplant',
    'Laminectomy', 'Liver transplant', 'Neck surgery', 'Kidney surgery', 'Ovarian surgery', 'Prostate surgery',
    'Rectal surgery', 'Small bowel surgery', 'Spleen surgery', 'Thoracic surgery', 'Thyroid and parathyroid surgery',
    'Vaginal hysterectomy', 'Exploratory laparotomy', 'Breast surgery', 'Cardiac surgery', 'Coronary artery bypass graft',
    'Craniotomy', 'Spinal fusion', 'Open reduction of fracture', 'Hip prosthesis', 'Knee prosthesis', 'Pacemaker surgery',
    'Peripheral vascular bypass surgery', 'Ventricular shunt', 'Herniorrhaphy', 'Laparotomy'
  ];

  const theatreOptions = [
    'OT NO. 1', 'OT NO. 2', 'OT NO. 3', 'OT NO. 4', 'OT NO. 5', 'OT NO. 6',
    'OT NO. 7', 'OT NO. 8', 'OT NO. 9', 'OT NO. 10', 'OT NO. 11', 'OT NO. 12',
    'ROBOTIC OT', 'C-SEC OT', 'MINOR OT', 'COSMETOLOGY OT', 'Others'
  ];

  // Adjust the index to match the pre/peri-operative question in your form
  const prePeriOperativeIndex = 4;

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleEvaluationChange = (index, field, value) => {
    const updatedEvaluations = [...formData.evaluations];
    if (field === 'yes' || field === 'no') {
      updatedEvaluations[index] = {
        yes: field === 'yes',
        no: field === 'no',
        remarks: updatedEvaluations[index].remarks,
      };
    } else {
      updatedEvaluations[index] = { ...updatedEvaluations[index], remarks: value };
    }

    // Show antibiotics field if "Yes" is selected for pre/peri-operative question
    const showAntibioticsField = index === prePeriOperativeIndex && field === 'yes';
    setFormData({ ...formData, evaluations: updatedEvaluations, showAntibioticsField });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data Submitted:', formData);
  };

  const formStyles = {
    container: { padding: '20px' },
    title: { marginBottom: '20px' },
    patientDetails: { marginBottom: '20px' },
    formGroup: { marginBottom: '10px' },
    label: { marginRight: '10px', fontWeight: 'bold' },
    input: { padding: '5px', width: '200px' },
    select: { padding: '5px', width: '210px' },
    table: { width: '100%', marginTop: '20px', borderCollapse: 'collapse' },
    th: { padding: '10px', border: '1px solid black', backgroundColor: '#f2f2f2' },
    td: { padding: '10px', border: '1px solid black' },
    submitBtn: { padding: '10px 20px', marginTop: '20px' },
  };

  return (
    <form style={formStyles.container} onSubmit={handleSubmit}>
      <h2 style={formStyles.title}>SSI Evaluation Tool</h2>

      <div style={formStyles.patientDetails}>
        <div style={formStyles.formGroup}>
          <label style={formStyles.label}>Patient's Name:</label>
          <input
            type="text"
            name="patient_name"
            value={formData.patient_name}
            onChange={handleInputChange}
            required
            style={formStyles.input}
          />
        </div>

        <div style={formStyles.formGroup}>
          <label style={formStyles.label}>Patient's ID:</label>
          <input
            type="text"
            name="patient_id"
            value={formData.patient_id}
            onChange={handleInputChange}
            required
            style={formStyles.input}
          />
        </div>

        <div style={formStyles.formGroup}>
          <label style={formStyles.label}>Age:</label>
          <input
            type="number"
            name="age"
            value={formData.age}
            onChange={handleInputChange}
            required
            style={formStyles.input}
          />
        </div>

        <div style={formStyles.formGroup}>
          <label style={formStyles.label}>Gender:</label>
          <select
            name="gender"
            value={formData.gender}
            onChange={handleInputChange}
            required
            style={formStyles.select}
          >
            <option value="">Select Gender</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Other">Other</option>
          </select>
        </div>

        <div style={formStyles.formGroup}>
          <label style={formStyles.label}>Procedure Name:</label>
          <select
            name="procedure_name"
            value={formData.procedure_name}
            onChange={handleInputChange}
            required
            style={formStyles.select}
          >
            <option value="">Select Procedure</option>
            {procedureOptions.map((procedure, index) => (
              <option key={index} value={procedure}>{procedure}</option>
            ))}
          </select>
        </div>

        <div style={formStyles.formGroup}>
          <label style={formStyles.label}>Operation Theatre:</label>
          <select
            name="operation_theatre"
            value={formData.operation_theatre}
            onChange={handleInputChange}
            required
            style={formStyles.select}
          >
            <option value="">Select OT</option>
            {theatreOptions.map((ot, index) => (
              <option key={index} value={ot}>{ot}</option>
            ))}
          </select>
        </div>

        <div style={formStyles.formGroup}>
          <label style={formStyles.label}>Date of Procedure:</label>
          <input
            type="date"
            name="procedure_date"
            value={formData.procedure_date}
            onChange={handleInputChange}
            required
            style={formStyles.input}
          />
        </div>
      </div>

      <table style={formStyles.table}>
        <thead>
          <tr>
            <th style={formStyles.th}>S.NO</th>
            <th style={formStyles.th}>ITEMS</th>
            <th style={formStyles.th}>YES</th>
            <th style={formStyles.th}>NO</th>
            <th style={formStyles.th}>REMARKS</th>
          </tr>
        </thead>
        <tbody>
          {/* SSI Evaluation Items go here */}
        </tbody>
      </table>

      {/* Conditional field for antibiotics */}
      {formData.showAntibioticsField && (
        <div style={formStyles.formGroup}>
          <label style={formStyles.label}>If Yes, Antibiotics given:</label>
          <input
            type="text"
            name="antibioticsGiven"
            value={formData.antibioticsGiven}
            onChange={handleInputChange}
            style={formStyles.input}
          />
        </div>
      )}

      <button type="submit" style={formStyles.submitBtn}>
        Submit
      </button>
    </form>
  );
};

export default SSIEvaluationForm;
