import React, { useState } from 'react';
import axios from 'axios';

const SurveillanceForm = () => {
  const [formData, setFormData] = useState({
    patientName: '',
    patientId: '',
    age: '',
    gender: '',
    dateOfAdmission: '',
    dateOfProcedure: '',
    admittingDepartment: '',
    department: '',
    procedureName: '',
    diagnosis: '',
    woundClass: '',
    papGiven: false,
    antibiotics: '',
    durationOfPAP: '',
    ssiEventOccurred: false,
    ssiEventDate: '',
    specificEvent: '',
    detected: '',
    microOrganisms: ['', ''],
    antibioticPatterns: [{ sensitive: '', resistant: '', intermediate: '' }],
    secondaryBSI: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/api/submit_form/', formData);
      console.log('Form submitted successfully:', response.data);
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Patient Name:</label>
        <input type="text" name="patientName" value={formData.patientName} onChange={handleChange} />
      </div>
      {/* Add other form fields here */}
      <div>
        <label>Pre/Peri-operative Antibiotic Prophylaxis (PAP) given:</label>
        <input type="checkbox" name="papGiven" checked={formData.papGiven} onChange={handleChange} />
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default SurveillanceForm;
