import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './MainPage.css';
import { FaUserNurse, FaUserMd, FaStethoscope } from 'react-icons/fa'; // Bootstrap icons for roles
import logo from './logo.png'; // Update if necessary

const MainPage = () => {
  const [activeSection, setActiveSection] = useState(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const navigate = useNavigate();

  // Define the slides array
  const slides = [
    { image: 'C:\Users\navya\OneDrive\Desktop\minor\src\image1.png', alt: 'Slide 1' },
    { image: 'C:\Users\navya\OneDrive\Desktop\minor\src\image2.png', alt: 'Slide 2' },
    { image: 'C:\Users\navya\OneDrive\Desktop\minor\src\image3.png', alt: 'Slide 3' },
  ];

  const handleSectionClick = (section) => {
    setActiveSection(activeSection === section ? null : section);
  };

  const handleRoleClick = (role) => {
    navigate(`/${role}`);
  };

  useEffect(() => {
    const slideInterval = setInterval(() => {
      setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
    }, 3000);

    return () => clearInterval(slideInterval);
  }, [slides.length]);

  return (
    <div className="main-page">
      {/* Header */}
      <header className="taskbar">
        <div className="logo-container">
          <img src={logo} alt="Jaypee Hospital Logo" className="logo" />
          <h1 className="hospital-name">Jaypee Hospital</h1>
        </div>
        <nav className="nav-bar">
          <ul className="nav-list">
            <li onClick={() => handleSectionClick('about')}>About Us</li>
            <li onClick={() => handleSectionClick('contact')}>Contact Us</li>
            <li onClick={() => handleSectionClick('locations')}>Locations</li>
          </ul>
        </nav>
      </header>

      {/* Slideshow */}
      <div className="slideshow">
        <img
          src={slides[currentSlide].image}
          alt={slides[currentSlide].alt}
          className="slideshow-image"
        />
      </div>

      {/* Main Content */}
      <div className="role-selection-container">
        <h2 className="choose-role-heading">Choose Your Role</h2>
        <div className="role-cards-container">
          <div className="role-card" onClick={() => handleRoleClick('nurse')}>
            <FaUserNurse className="role-icon" />
            <h3>Nurse</h3>
          </div>
          <div className="role-card" onClick={() => handleRoleClick('junior-doctor')}>
            <FaStethoscope className="role-icon" />
            <h3>Junior Doctor</h3>
          </div>
          <div className="role-card" onClick={() => handleRoleClick('senior-doctor')}>
            <FaUserMd className="role-icon" />
            <h3>Senior Doctor</h3>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MainPage;
